        <script type="text/javascript">
            function setTitle(title, button)
            {
                $('.modalSimpan').html(button);
                $('.modalLabel').html(title);
            }

            $(document).ready(function() {

                var dataFull;
                var tanggal;
                var role = '<?php echo $this->session->user['role'] ?>';


                $(document).on('submit', 'form', function(event) {
                    event.preventDefault();

                    var data = new FormData(this);

                    $.ajax({
                        url: '<?php echo base_url('home/diklat/index') ?>/'+id,
                        type: 'POST',
                        dataType: 'JSON',
                        cache: false,
                        processData : false,
                        contentType: false,
                        data: data,
                        beforeSend: function()
                        {
                            btnLoading($('.btnSimpan'));
                            cleanError();
                        },
                        complete: function()
                        {
                            btnNormal($('.btnSimpan'));
                        },
                        success: function(res)
                        {
                            if (!res.status) 
                            {
                                getError(res)
                            }
                            else
                            {
                                cleanError();
                                oTable.api().ajax.reload();

                                toastr[res.status](res.message);
                                $('#modal-delete').modal('hide');
                            }
                        }
                    })

                });

                var tanggal = '<?php echo $profile->pesertaTanggalLahir ?>';

                $('.datePicker').val(moment(tanggal).format('dddd, D MMMM YYYY'));

                $('.datePicker').datepicker({
                    rtl: KTUtil.isRTL(),
                    todayHighlight: true,
                    orientation: "bottom left",
                }).on('hide', function(e) {
                    $('.datePicker').val(moment(tanggal).format('dddd, D MMMM YYYY'));
                });;

                $(document).on('change', '.datePicker', function(event) {
                    tanggal = $(this).val();

                    $('.datePicker').val(moment(tanggal).format('dddd, D MMMM YYYY'));
                });

                $('#kt_daterangepicker_2').daterangepicker({
                    minDate: moment('<?php echo date('Y-m-d') ?>').format('MM/DD/YYYY'),
                    buttonClasses: ' btn',
                    applyClass: 'btn-primary',
                    cancelClass: 'btn-secondary'
                }, function(start, end, label) {
                    $('#kt_daterangepicker_2 .form-control').val( moment(start).locale('id').format('dddd, D MMMM YYYY') + ' - ' + moment(end).locale('id').format('dddd, D MMMM YYYY'));
                });

                $('#kt_daterangepicker_1').daterangepicker({
                    minDate: moment('<?php echo date('Y-m-d') ?>').format('MM/DD/YYYY'),
                    buttonClasses: ' btn',
                    applyClass: 'btn-primary',
                    cancelClass: 'btn-secondary'
                }, function(start, end, label) {
                    $('#kt_daterangepicker_1 .form-control').val( moment(start).locale('id').format('dddd, D MMMM YYYY') + ' - ' + moment(end).locale('id').format('dddd, D MMMM YYYY'));
                });

                oTable = $('#table').dataTable({
                    processing: true,
                    serverSide: true,
                    scrollX: false,
                    dom: '<"top">lrt<"bottom"ip>',
                    lengthMenu: [15, 30],
                    order: [[1, "asc"]],
                    'searching'   : true,
                    // pagingType: 'numbers',
                    language:{
                        "search":"Pencarian : ",
                        "info":           "<b>Menampilkan _START_ sampai _END_ dari _TOTAL_ Data</b>",

                    },
                    ajax: {
                        'type' : 'GET',
                        'url' : '<?php echo current_url();?>',
                        'dataSrc': function(json)
                        {
                            return dataFull = json.data;
                        },
                    },
                    drawCallback: function(settings) {
                        $('[data-toggle="kt-tooltip"]').tooltip();
                    },
                    columnDefs: [{visible: false, target: 0}],
                    columns: [
                    {
                        data: null, searchable: false, orderable: false, render: function (data) {

                            var btnDetail =  `<a class="details-control btn btn-sm btn-clean btn-icon btn-icon-md">
                            <i style="color: #20c997;" class="la la-toggle-off"></i></a>`;

                            return btnDetail;
                        }
                    },
                    {data: 'diklatNama'},
                    {data: 'diklatTempatPelatihan'},
                    {
                        data: null, searchable: false, orderable: false, render: function (data) {

                            btnDetail = '( '+data.diklatJumlah+' / '+data.diklatKuota+' )';

                            return btnDetail;
                        }
                    },
                    {data: 'diklatJumPendaftar', searchable: false, orderable: false},
                    {
                        data: null, searchable: false, orderable: false, render: function (data) {

                            if (data.diklatStatus == '0') 
                            {
                                btnDetail = '<span class="kt-badge  kt-badge--danger kt-badge--inline kt-badge--pill">Ditutup</span>';
                            }
                            else
                            {
                                btnDetail = '<span class="kt-badge  kt-badge--info kt-badge--inline kt-badge--pill">Dibuka</span>';
                            }

                            return btnDetail;
                        }
                    },
                    {
                        data: null, searchable: false, orderable: false, render: function (data, style, row) {

                            var btnDetail = '';

                            if (row.diklatStatus == 1 && row.diklatIsDaftar == '0' && role == 'peserta' && parseInt(row.diklatJumlah) < parseInt(row.diklatKuota)) 
                            {
                                btnDetail = ` <a href="#" data-id="`+row.diklatId+`" class="btnDetailDaftar btn btn-sm btn-label-facebook"><i class="fa fa-paper-plane"></i> Daftar</a>`;
                            }
                            else if (row.diklatStatus == 0) 
                            {
                                btnDetail = ``;
                            }
                            else if(row.diklatIsAcc == '1')
                            {
                                btnDetail = ` <span class="kt-badge kt-badge--inline kt-badge--success">Diverifikasi</span>`;
                            }
                            else if(row.diklatIsAcc == '0' && row.diklatIsDaftar == '1')
                            {
                                btnDetail = ` <span class="kt-badge kt-badge--inline kt-badge--primary">Menunggu Persetujuan</span>`;
                            }

                            if (role == '') 
                            {
                                btnDetail = ` <a href="<?php echo base_url('login/login') ?>" class="btn btn-sm btn-label-facebook"><i class="fa fa-key"></i> Login</a>`;
                            }
                            else if (role == 'admin' || role == 'pegawai') 
                            {
                                btnDetail = ``;
                            }

                            return btnDetail;
                        }
                    },
                    ],
                });

                $("#btn-cari").click(function () {
                    oTable.fnFilter($("#field-cari").val());
                });

                $("#field-cari").on('keyup', function(e) {
                    var key = e.which;
                    if (key == 13) 
                    { 
                        oTable.fnFilter($("#field-cari").val());
                    }
                });

                $('tbody').on('click', 'td a.details-control', function () {
                    var tr = $(this).closest('tr');
                    var row = oTable.api().row( tr );

                    if ( row.child.isShown() ) {
                        $(this).html('<i style="color: #20c997;" class="la la-toggle-off"></i>')
                        row.child.hide('slow');
                        tr.removeClass('shown');
                    }
                    else {
                        row.child( format(row.data()) ).show();
                        tr.addClass('shown');

                        $('[data-toggle="tooltip"]').tooltip();  
                        $(this).html('<i style="color: #20c997;"  class="la la-toggle-on"></i>')

                    }
                } 
                );

                function format ( d ) 
                {
                    var text = $('<div/>')
                    .addClass( 'text-center' )
                    .html( '<i class="fa fa-spinner fa-spin"></i> Loading ...' );

                    $.ajax({
                        url: '<?php echo current_url() ?>',
                        type: 'POST',
                        dataType: 'JSON',
                        data: {
                            'mengajarDiklatId': d.diklatId,
                            'status' : 'getPengajar'
                        },
                        success: function(res)
                        {
                            text.html( templateDetail(d, res) ).removeClass( 'text-center' );

                            $('[data-toggle="kt-tooltip"]').tooltip();  
                        },
                        error: function(res)
                        {
                            text
                            .html('<div class="text-center"><i class="fa fa-signal"></i> Periksa Koneksi anda</div>')
                            .removeClass( 'loading' );
                        }
                    })

                    return text;
                }

                function templateDetail(d, data)
                {
                    var pengajar = '';
                    var pendaftar = '';
                    var peserta = '';
                    var statusPendaftaran = '';

                    $.each(data.pengajar, function(index, val) {
                        var foto = (val.pengajarFoto == null)?'<?php echo base_url('assets/media/users/default.jpg') ?>':'<?php echo base_url('assets/upload/images') ?>/'+val.pengajarFoto;

                        pengajar += ` <a href="#" class="kt-media kt-media--sm kt-media--circle" data-toggle="kt-tooltip" data-skin="brand" data-placement="top" title="" data-original-title="`+val.pengajarNama+`">
                        <img src="`+foto+`" alt="image">
                        </a>`
                    });

                    if (d.diklatStatus == 1 && d.diklatIsDaftar == '0' && role == 'peserta' && d.diklatJumlah < d.diklatKuota) 
                    {
                        // statusPendaftaran = ` <a href="#" data-id="`+d.diklatId+`" class="btnDetailDaftar btn btn-label-facebook"><i class="fa fa-paper-plane"></i> Daftar</a>`;
                    }

                    if (role == '') 
                    {
                        // statusPendaftaran = ` <a href="<?php echo base_url('login/login') ?>" class="btn btn-label-facebook"><i class="fa fa-plus"></i> Login</a>`;
                    }

                    $.each(data.pendaftar, function(index, val) {

                        var foto = (val.pesertaFoto == null)?'<?php echo base_url('assets/media/users/default.jpg') ?>':'<?php echo base_url('assets/upload/images') ?>/'+val.pesertaFoto;


                        if (val.pendaftaranIsAcc == '1') 
                        {
                            peserta += ` <a href="#" class="kt-media kt-media--sm kt-media--circle" data-toggle="kt-tooltip" data-skin="brand" data-placement="top" title="" data-original-title="`+val.pesertaNama+`">
                            <img src="`+foto+`" alt="image">
                            </a>`
                        }
                        else
                        {
                            pendaftar += ` <a href="#" class="kt-media kt-media--sm kt-media--circle" data-toggle="kt-tooltip" data-skin="brand" data-placement="top" title="" data-original-title="`+val.pesertaNama+`">
                            <img src="`+foto+`" alt="image">
                            </a>`
                        }
                    });
                    return `<div class="kt-portlet__body kt-portlet__body--fit">
                    <div class="kt-widget kt-widget--project-1">


                    <div class="kt-widget__body">
                    <div class="kt-widget__stats">
                    <div class="kt-widget__item">
                    <span class="kt-widget__date">
                    Tanggal Pendaftaran
                    </span>
                    <div class="kt-widget__label">
                    <span class="btn btn-label-brand btn-sm btn-bold btn-upper">`+moment(d.diklatTanggalPendaftaran).locale('id').format('dddd, D MMMM YYYY') +`</span>
                    </div>
                    </div>

                    <div class="kt-widget__item">
