                $(document).on('click', '.btnShowModal', function(event) {
                    id = '';

                    cleanError();
                    $('#formData').trigger('reset');
                });

                $(document).on('click', '.btnDaftar', function(event) {

                    var ini = $(this);

                    $.ajax({
                        url: '<?php echo current_url() ?>',
                        type: 'POST',
                        dataType: 'JSON',
                        data: {
                            diklatId: id,
                            status: 'sendPendaftaran'
                        },
                        beforeSend: function(){
                            btnLoading(ini);
                        },
                        success: function(res)
                        {
                            if (res.status == 'success') 
                            {
                                oTable.api().ajax.reload();
                                $('.lblError').html('');
                                ini.html(res.message);
                            }
                            else
                            {

                                $('.lblError').html(res.message);
                                btnNormal(ini);
                            }
                        },
                    })

                });

                var id;
                $(document).on('click', '.btnDetailDaftar', function(event) {
                    id = $(this).data('id');
                    console.log(id)

                    $('#modal-delete').modal('show');

                });

                function getError(data)
                {
                    $.each(data.error, function(index, el) 
                    {
                        $('.'+index).html(el);
                    }); 
                }

                function cleanError()
                {
                    $('.cleanError').html('');
                }


                var btnText;
                function btnLoading(selector)
                {
                    btnText = selector.html();
                    selector.html('<i class="fa fa-spinner fa-spin"></i> Loading .....');
                    selector.attr('disabled', 'true');
                }

                function btnNormal(selector)
                {
                    selector.html(btnText);
                    selector.removeAttr('disabled');
                }

                toastConfig();
                function toastConfig(){
                    toastr.options = {
                        "closeButton": false,
                        "debug": false,
                        "newestOnTop": false,
                        "progressBar": false,
                        "positionClass": "toast-top-right",
                        "preventDuplicates": true,
                        "onclick": null,
                        "showDuration": "300",
                        "hideDuration": "1000",
                        "timeOut": "5000",
                        "extendedTimeOut": "1000",
                        "showEasing": "swing",
                        "hideEasing": "linear",
                        "showMethod": "fadeIn",
                        "hideMethod": "fadeOut"
                    }
                }

            });
        </script>